	public static boolean isValid(String s) {
        
        boolean[] arr = new boolean[3];
        String last = "";
        if (s.length() == 1) {
            return false;
        }

        Map<Character, Integer> map = new HashMap<Character, Integer>();
        map.put('(', 0);
        map.put('{', 0);
        map.put('[', 0);
        map.put(')', 0);
        map.put('}', 0);
        map.put(']', 0);

        for (int i = 0; i < s.length(); i++) {
            char curr = s.charAt(i);
            int len = last.length();
            map.merge(curr, 1, Integer::sum);
            if (curr == '(' || (len != 0 && curr == ')' && last.charAt(len-1) == '(') ) {
                arr[0] = !arr[0];
            } else if (curr == '{' || (len != 0 && curr == '}' && last.charAt(len-1) == '{')) {
                arr[1] = !arr[1];
                
            } else if (curr == '[' || (len != 0 && curr == ']' && last.charAt(len-1) == '[')) {
                arr[2] = !arr[2];
               
            }

            if ((len != 0)&& ((curr == ')' && last.charAt(len-1) == '(') ||
            (curr == '}' && last.charAt(len-1) == '{') ||
            (curr == ']' && last.charAt(len-1) == '['))) {
                last = last.substring(0, len-1);
                 
            }

            if (curr == '(' || curr == '{' || curr == '[') {
                last += curr;
                 
            }
        }

        if ((!map.get('(').equals(map.get(')'))) 
        || (!map.get('{').equals(map.get('}'))) 
        || (!map.get('[').equals(map.get(']')))) {
            return false;
        }
        for (int i = 0; i < arr.length; i++) {
            if (arr[i] == true) {
                return false;
            }
        }
        return true;
    }
	
	public static boolean isValid1(String s) {
		
		Stack<Character> st = new Stack<Character>();
		
		for (char c: s.toCharArray()) {
			if (c == '(') {
				st.push(')');
			} else if (c == '{') {
				st.push('}');
			} else if (c == '[') {
				st.push(']');
			} else if (st.isEmpty() || st.pop() != c) {
				return false;
			}
		}
		
		System.out.println(st.isEmpty());
		return st.isEmpty();
		
	}
